INSERT INTO `products` VALUES (6,'Bộ Bàn Ăn Scania (Màu Tự Nhiên, Mặt Vân Đá, 140)',100,13990000,11990000,'<p><span style=\"font-family: \'times new roman\', times, serif;\"><strong>BỘ&nbsp;B&Agrave;N ĂN V&Acirc;N Đ&Aacute; TỰ NHI&Ecirc;N&nbsp;<u>SCANIA</u></strong></span></p>\r\n<h2><span style=\"font-family: \'times new roman\', times, serif;\"><strong>1. TH&Ocirc;NG SỐ KỸ THUẬT</strong></span></h2>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Nội dung sản phẩm\" src=\"https://file.hstatic.net/200000065946/file/z6597452262947_58a5437d38425367e5b0c8e62af26737_57c61a20a44b4815bf4ac9abd4b97737_2048x2048.jpg\" alt=\"bộ b&agrave;n ăn scania\" width=\"252\" height=\"252\"></span></p>\r\n<p>&nbsp;</p>\r\n<h2><span style=\"font-family: \'times new roman\', times, serif;\"><strong>2. CHI TIẾT VẬT LIỆU</strong></span></h2>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\"><strong>Khung ch&acirc;n : Gỗ cao su tự nhi&ecirc;n</strong></span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">B&agrave;n ăn gia đ&igrave;nh l&agrave;m từ chất liệu&nbsp;<a href=\"https://moho.com.vn/blogs/noi-that-ung-dung/tim-hieu-ve-cac-loai-go-tu-nhien-thong-dung\" target=\"_blank\" rel=\"noopener\"><u><strong>gỗ cao su</strong></u></a>&nbsp;tự nhi&ecirc;n đảm bảo độ chắc chắn cao, chống c&ocirc;ng v&ecirc;nh, mối mọt. M&agrave;u gỗ bắt mắt, đẹp tạo n&eacute;t hiện đại&nbsp;.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\"><strong>Mặt b&agrave;n : Gỗ c&ocirc;ng nghiệp phủ Melamine v&acirc;n đ&aacute; tự nhi&ecirc;n&nbsp;</strong></span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Lớp phủ Melamine c&oacute; thể t&aacute;i tạo ch&acirc;n thật v&acirc;n đ&aacute; tự nhi&ecirc;n mang lại vẻ đẹp sang trọng v&agrave; hiện đại.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Nội dung sản phẩm\" src=\"https://file.hstatic.net/200000065946/file/z6526735263438_ce3f3e4fb92840a04b2488b8a5ceffe2_1024x1024.jpg\" width=\"372\" height=\"186\"></span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\"><em>* Một số sản phẩm sử dụng gỗ tự nhi&ecirc;n c&oacute; thể xuất hiện mắt gỗ ngẫu nhi&ecirc;n v&agrave; v&acirc;n gỗ tr&ecirc;n bề mặt mỗi sản phẩm sẽ kh&aacute;c nhau &ndash; điều n&agrave;y l&agrave; ho&agrave;n to&agrave;n tự nhi&ecirc;n, tạo n&ecirc;n sự độc đ&aacute;o ri&ecirc;ng cho từng sản phẩm. V&igrave; vậy MOHO sẽ kh&ocirc;ng &aacute;p dụng c&aacute;c ch&iacute;nh s&aacute;ch đổi trả, bảo h&agrave;nh v&agrave; bảo tr&igrave; cho c&aacute;c trường hợp n&agrave;y.</em></span></p>\r\n<p>&nbsp;</p>\r\n<h2><span style=\"font-family: \'times new roman\', times, serif;\"><strong>3.&nbsp;ĐẶC ĐIỂM NỔI BẬT</strong></span></h2>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Mang phong c&aacute;ch hiện đại v&agrave; tinh tế, B&agrave;n Ăn SCANIA v&acirc;n đ&aacute; l&agrave; lựa chọn ho&agrave;n hảo cho kh&ocirc;ng gian ph&ograve;ng ăn sang trọng. Sự kết hợp giữa mặt b&agrave;n v&acirc;n giả đ&aacute; cao cấp v&agrave; khung ch&acirc;n từ gỗ cao su tự nhi&ecirc;n vững chắc kh&ocirc;ng chỉ mang đến vẻ đẹp thẩm mỹ m&agrave; c&ograve;n đảm bảo độ bền vượt trội theo thời gian.&nbsp;</span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\"><strong>Mặt b&agrave;n gỗ c&ocirc;ng nghiệp phủ Melamine v&acirc;n đ&aacute; tự nhi&ecirc;n</strong></span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Tạo cảm gi&aacute;c sang trọng, hiện đại như đ&aacute; thật m&agrave; nhẹ&nbsp;hơn rất nhiều.&nbsp;</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"Nội dung sản phẩm\" sr